(window.webpackJsonp=window.webpackJsonp||[]).push([[15],{421:function(v,_,p){"use strict";p.r(_);var t=p(62),e=Object(t.a)({},(function(){var v=this,_=v.$createElement,p=v._self._c||_;return p("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[p("h1",{attrs:{id:"尾声"}},[p("a",{staticClass:"header-anchor",attrs:{href:"#尾声"}},[v._v("#")]),v._v(" 尾声")]),v._v(" "),p("p",[v._v("萤身着一袭雪白的新娘和服，沉默地跟随父亲沿着石径走向神社。父亲在她婚礼前几天才从都城回到道寺宫，他们还没有机会交谈。萤并不期待向父亲解释自己，而父亲也没有召见她要求解释。尽管鹤族仍控制着群岛，她却在救援中不必要地让自己陷入危险，并危及大道寺宇治的精锐部队。她显得鲁莽。")]),v._v(" "),p("p",[v._v("为了避免未来再出现这种鲁莽行为，是时候让她结婚并诞下继承人了。她将嫁给九头寺葛信，以化解始于俊望光叔叔的纷争，并确保邻近的狐族的忠诚。")]),v._v(" "),p("p",[v._v("她没有抱怨。她做了正确的事，即使她无法告诉父亲，是她与蝎族的临时联盟和伪造文件帮助鹤族确保了白帆城堡的安全。如果她能帮助治愈俊望光自她认识他以来一直后悔的伤口，那么她将毫不犹豫地去做。")]),v._v(" "),p("p",[v._v("她没有更好的伴侣人选。毕竟，间由史嘉智子已经结婚了。")]),v._v(" "),p("p",[v._v("在道寺之神神社的屋顶下，萤三次从清酒杯中啜饮，然后将杯子递给葛信，每次用不同大小的杯子重复这个过程两次。他们的父母也做了同样的事情，象征着道寺和九头寺家族之间的结合。")]),v._v(" "),p("p",[v._v("尽管葛信穿着华丽的服饰，他柔软的棕色波浪发让他看起来像是刚从家族心爱的树林中散步归来。他身上散发着松树和新鲜雨水的味道。他浅棕色的眼睛在神社内圣坛的映照下显得金黄。")]),v._v(" "),p("p",[v._v("萤确实很幸运。葛信机智、迷人且英俊，但最重要的是，自从他和他的家人随着春雪融化来到宫殿以来，他对她一直很友善。")]),v._v(" "),p("p",[v._v("在祭司诵完祝福后，他们成为了夫妻，九头寺葛信正式成为道寺葛信，未来鹤族冠军的配偶。")]),v._v(" "),p("p",[v._v("当他们从神社返回时，俊望光向他们表示祝贺。“祝贺我最喜欢的侄女。”他看了看葛信，然后轻声说道：“我从未忘记龙田夫人，我很抱歉。”")]),v._v(" "),p("p",[v._v("葛信把手放在萤的手臂上，点了点头。“现在鹤族和狐族将并肩而立。”")]),v._v(" "),p("p",[v._v("如果她与葛信的婚姻能为两个家族带来和平，那么这只是微不足道的牺牲。和平可能很难。与她曾在白帆城堡所经历的相比，婚礼似乎是和解的轻松途径。爱情仍可能在他们之间绽放，如果没有，她至少尊重葛信。")]),v._v(" "),p("p",[v._v("尽管她运气不错，萤仍希望嘉智子也能出席。邀请已发给了所有其他大族冠军及其配偶，但帮助挫败海盗接管飞鱼群岛的使者是否有时间参加婚礼招待会则完全是另一回事。")]),v._v(" "),p("p",[v._v("她和葛信被介绍给来访的贵宾和贵族时，时间仿佛永恒。佐冷身穿帝制翡翠和金色长袍，胸前绣着翡翠冠军的徽章，静静地坐在她身旁。当祝福者走近讲台时，他们更多的是在寻求佐冷的好感，而不是向道寺萤本人表示祝贺。")]),v._v(" "),p("p",[v._v("当间由史嘉智子和第三十八代梵帝走近新娘和新郎时，传令官宣布了他们的到来。")]),v._v(" "),p("p",[v._v("葛信几乎无法抑制自己的兴奋，像一只被尾巴摇动的小狗。“天皇陛下！”他惊呼着，在讲台上匍匐在地。")]),v._v(" "),p("p",[v._v("萤也照做了，她的心跳加速。当她鼓起勇气抬起目光时，她看到嘉智子纤细的身形包裹在一件正式的黑色和服中。绣着的紫罗兰在她的袖子和下摆展开——在花语中，它们象征着诚实，这似乎对蝎族来说太过奢望。嘉智子的头发用精致的珠宝整齐地别在脑后，简单而优雅。这可能是第一次，这个女人的优雅没有胜过萤。")]),v._v(" "),p("p",[v._v("在她黑色的蕾丝面具后面，嘉智子深邃的眼睛再次注视着萤，没有流露出任何情感，仿佛她们从未分享过那个激情的吻。")]),v._v(" "),p("p",[v._v("“请允许我代表整个蝎族向你们表示祝贺，”嘉智子愉快地说道，依次向她们深深鞠躬。“但事实上，我必须再次向您的夫人表示感谢。从一开始，道寺萤就警告螳螂族船员他们领袖的愚蠢。正是由于她的努力，螳螂族才愿意投降，伤亡如此之少——并且我作为使者得到了保护。”")]),v._v(" "),p("p",[v._v("通常，天皇不需要说话来表达他的祝福——他的存在本身就是足够的赞誉。但梵帝礼貌地向这对夫妇鞠躬，然后特别向萤鞠躬。萤脸红了，鞠躬。她一直专注于嘉智子，几乎忘记了天皇的存在。")]),v._v(" "),p("p",[v._v("“上天感谢你，道寺萤女士。感谢你的仁慈以及你为我的仆人提供的保护。”")]),v._v(" "),p("p",[v._v("她从未梦想过会得到天皇的赞扬，更不用说在她所有氏族面前。这可能是嘉智子在她婚礼当天能送给她的最珍贵的礼物。")]),v._v(" "),p("p",[v._v("她毫无异议地接受了赞扬，因为没有人会反驳天皇，即使在其他情况下谦虚是惯例。当嘉智子和天皇离开时，萤和葛信更加深深地鞠躬。")]),v._v(" "),p("p",[v._v("“你做得很好，萤，”父亲在祝福者之间轻声说道。“但你还有很长的路要走。”")]),v._v(" "),p("p",[v._v("萤看向聚集的鹤族武士，他们中的许多人正在祝贺大道寺宇治在白帆城堡的出色突袭。")]),v._v(" "),p("p",[v._v("“我知道。”作为未来冠军，她将走的道路并不容易。")]),v._v(" "),p("p",[v._v("几个小时过去了，似乎无穷无尽的食物和美酒摆满了餐桌，越来越多的祝福者前来表示敬意。按照习俗，萤从纯白和服换成了越来越鲜艳的长袍，总共换了三次，突出了鹤族的奢华以及萤从神圣仪式回归正常生活的个人转变。")]),v._v(" "),p("p",[v._v("当派对延伸到黄昏，庭院周围的石灯笼被点亮时，她刚刚从第三次也是最后一次换装回来，看到嘉智子走在离人群几步之外的小径上。")]),v._v(" "),p("p",[v._v("她应该克制自己，毕竟几小时前她才刚刚与葛信结合。但这可能是她再次单独见到嘉智子的唯一机会。她们之间还有太多未说出口的话。")]),v._v(" "),p("p",[v._v("尽管理智告诉她不该这么做，她还是跟随嘉智子走进了花园。各种颜色的花瓣铺满了小径，像雪花一样在石头之间飘荡。这个由花朵和树木组成的迷宫在她心中留下了许多痕迹，她知道该走哪条路才能在嘉智子到达湖边之前拦住她。")]),v._v(" "),p("p",[v._v("她发现嘉智子站在一棵百年樱花的阴影下，她黑色的身影在花园柔和的暮色中划出一道鲜明的线条。一条小溪在树根周围潺潺流淌，周围的花朵散发出浓郁的香气。")]),v._v(" "),p("p",[v._v("几天后，所有这些美丽都将改变。她们在一起的这一刻也同样短暂。")]),v._v(" "),p("p",[v._v("“你没有把所有的功劳都据为己有，”萤开口说道，声音中毫不掩饰震惊。即使她们已经成为了某种朋友、盟友，甚至是恋人，这也不会阻止嘉智子在看到机会时抓住它。“为什么没有？”她期待着一个谎言，或者最多是半真半假。")]),v._v(" "),p("p",[v._v("嘉智子将扇子插入腰带，缩短了她们之间的距离。“源藤船长是按照我的命令行事，而不是赖朝大人的。”")]),v._v(" "),p("p",[v._v("嘉智子的话像在格斗练习中踢到肚子一样，让萤完全喘不过气来。这个声明太大胆了，不可能是假的。如果这是真的，那么那些鹤族武士的死亡就应归咎于嘉智子。她甚至愿意将天皇的仆人卷入战争。")]),v._v(" "),p("p",[v._v("萤曾发誓要保护嘉智子，甚至愿意为她的保护对象做出终极牺牲。然而，嘉智子却愿意牺牲他人的生命来推进自己的议程。“我知道你在为自己的目的行事，但是……”这就是嘉智子需要解决的问题。她认为现在可以乞求萤的原谅吗？")]),v._v(" "),p("p",[v._v("“为我氏族效力的武士因你而死，”萤嘶嘶地说道。“佐佐木家族的武士，我的信使……你为什么要做这样的事？”")]),v._v(" "),p("p",[v._v("“那不在计划之内，”嘉智子回答道，声音颤抖。她处于防守状态，这对她来说很不寻常。“召唤雾气本应让源藤船长在无伤亡的情况下夺取城堡。我不知道他会如此残忍。”")]),v._v(" "),p("p",[v._v("有可能嘉智子真的不知道。但无论她是否无知，无辜的鲜血因她而流。")]),v._v(" "),p("p",[v._v("“一旦城堡安全地掌握在稻妻号船员手中，我将开始谈判，这将拖延到鹤族看起来随时可能发动攻击为止。然后，我将宣布源藤船长从城堡撤军。螳螂族仍将拥有首先夺取城堡并击败鹤族的吹嘘权利，而我作为和平的仲裁者将向天皇证明我的用处。”")]),v._v(" "),p("p",[v._v("这一切都完美地解释了。这应该是一个简单的事情。“但你没想到我会跟着来。”萤补充道，仍然因愤怒而颤抖。")]),v._v(" "),p("p",[v._v("“是的。你破坏了我所有的计划，”嘉智子点点头。")]),v._v(" "),p("p",[v._v("萤应该就这样离开。她有一个丈夫要照顾，还有她的氏族，他们中的任何人都比嘉智子更高尚。但他们中没有一个人像嘉智子那样理解她。")]),v._v(" "),p("p",[v._v("“然而，你曾想过，如果我不必独自为权力而战，我会是什么样的人。到那时，你已经来不及帮助我了。我不能把你更深地卷入我自己设计的漩涡中。”嘉智子看向远处的湖泊。")]),v._v(" "),p("p",[v._v("“在城堡的最后一晚，我不知道会发生什么——如果源藤会杀了我，一了百了。我无法想象你会来找我，更不用说你会愿意为我牺牲生命。我欠你一个巨大的恩情。”")]),v._v(" "),p("p",[v._v("嘉智子是个骗子，一个用他人生命赌博的阴谋家。萤从一开始就不该信任这个女人，这是正确的。")]),v._v(" "),p("p",[v._v("“那骰子呢？你作弊螳螂族是为了单独与源藤交谈吗？”")]),v._v(" "),p("p",[v._v("“没有，”嘉智子呼吸着说。“那是你的运气，萤。那是命运。命运让骰子落在它们该落的地方，命运让我有你保护。”")]),v._v(" "),p("p",[v._v("尽管一切如此，嘉智子最终站在了萤这边。如果嘉智子心中没有一些善良，如果她不愿意在权力摆在面前时转身离开，萤就不会活下来。让源藤在最后逃脱会更容易，但嘉智子冒着攻击源藤的风险，给萤一个机会结束他。")]),v._v(" "),p("p",[v._v("她必须相信萤会在她未能一击致命时救她。")]),v._v(" "),p("p",[v._v("嘉智子犯了可怕的错误，但她并非不可救赎。一起，鹤族和蝎族，她们能为帝国做的比分开时更多。")]),v._v(" "),p("p",[v._v("在嘉智子身边，萤可以提醒蝎族什么是服务，什么是值得信赖的武士。还有其他人理解她。")]),v._v(" "),p("p",[v._v("也许，有嘉智子在她身边，鹤族可以记住在你迷失时有人帮助你的感觉，在你受伤时安慰你。接受你所有的不完美。")]),v._v(" "),p("p",[v._v("萤的愤怒随着春风穿过树枝而平息，粉色的花瓣洒在她们身上。“债务已经还清了。”")]),v._v(" "),p("p",[v._v("在有人不可避免地闯入花园的这部分之前，她们只剩下片刻。这是她离开的机会，在太晚之前转身背对蝎族之母，以免她们被发现在一起，过于亲密而不合礼仪。她们都知道，即使是暗示鹤族继承人与蝎族冠军的妻子相爱，也会引发丑闻。两人都会蒙羞，可能被氏族驱逐。")]),v._v(" "),p("p",[v._v("她所学的一切都警告她要远离这个女人，但现在她只想紧紧抱住她。")]),v._v(" "),p("p",[v._v("“不要让我孤单。留在我身边。求求你，”嘉智子低声说道，说出了她不该存在的部分，她想要否认的软弱。“我会与你并肩作战，而不是与你对抗。”")]),v._v(" "),p("p",[v._v("永远不要相信蝎族，嘉智子曾这样警告她。")]),v._v(" "),p("p",[v._v("萤抬起嘉智子的下巴，手指抚过她的脸颊。")]),v._v(" "),p("p",[v._v("萤会信任面具后的女人。")]),v._v(" "),p("p",[v._v("她在樱花的纷飞中倾身吻了嘉智子。")])])}),[],!1,null,null,null);_.default=e.exports}}]);