(window.webpackJsonp=window.webpackJsonp||[]).push([[21],{426:function(v,_,p){"use strict";p.r(_);var t=p(62),e=Object(t.a)({},(function(){var v=this,_=v.$createElement,p=v._self._c||_;return p("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[p("h1",{attrs:{id:"第五章"}},[p("a",{staticClass:"header-anchor",attrs:{href:"#第五章"}},[v._v("#")]),v._v(" 第五章")]),v._v(" "),p("p",[v._v("白帆堡的壁画横跨整个墙面。在城堡的墙脚下，一棵扭曲多节的杜松树紧紧依附在岩石峭壁上。悬崖旁波涛汹涌，几笔颜料勾勒出在海湾上空翱翔的鸟群。东方的地平线上，金色的阳光从初升的太阳中散发出来，但吸引人目光的却是一个高大魁梧的武士，他双腿大张地坐在单层台阶上的折叠椅上。一个篡位者。")]),v._v(" "),p("p",[v._v("大副走上前，为这场嘲弄般的宫廷仪式充当传令官。“跪拜源藤，雷号船长，白帆堡之主，飞鱼姬的祝福者，旋风镰刀流的传奇决斗者，南海的掠夺者，葡萄牙人的征服者，以及蛇神伊久之子！”")]),v._v(" "),p("p",[v._v("除了船长之外，所有这些头衔都是自封的，萤确信。")]),v._v(" "),p("p",[v._v("源藤本人是一个下巴结实、胡须浓密的大汉。在他身旁，本该悬挂佐佐木冲本的礼仪盔甲的地方，却挂着一件鲜绿色的胸甲和头盔。头盔顶部的两根金色尖刺像触角一样竖起，每根都近一尺长。红色和青绿色的线穿过肩甲，与他所穿的丝绸内甲相配，仿佛他的家族已经处于战争状态，他随时可能需要穿上盔甲。")]),v._v(" "),p("p",[v._v("他一边看着嘉智子和萤跪着走近，一边脸上浮现出一丝讥讽的笑容。")]),v._v(" "),p("p",[v._v("“蝎族间由史嘉智子，第三十八代梵帝天皇的使者，”大副宣布道，显然他在关键时刻还是能做出恰当的介绍的。")]),v._v(" "),p("p",[v._v("嘉智子向船长微微鞠躬。“请允许我介绍我的保镖，鹤族继承人，道寺萤。”")]),v._v(" "),p("p",[v._v("萤强迫自己以完美的礼貌效仿，但她的背后传来一阵窃笑。另一名螳螂族武士讥讽道：“鹤族没收到消息吗？”一个听起来像大鱼的人补充道：“他们派来了继承人？他们是白痴吗？”")]),v._v(" "),p("p",[v._v("在螳螂族中，似乎对敌人的正常礼节并不适用。但表现出恼怒有失鹤族风范。")]),v._v(" "),p("p",[v._v("“所以，鹤族躲在蝎族背后，畏惧螳螂族的力量！”源藤一边笑着，一边将肘部靠在膝盖上。")]),v._v(" "),p("p",[v._v("周围的螳螂族武士们开怀大笑。许多人看起来像是刚从船上下来，急需刮胡子或洗澡，或者两者都需要。这群乌合之众似乎只对金钱表示尊重。")]),v._v(" "),p("p",[v._v("“尽管我们有不速之客，但让我欢迎你，美丽的嘉智子，来到我的海滨城堡！”")]),v._v(" "),p("p",[v._v("如果萤认为他会同意以文明的方式进行决斗，她当时就会挑战他对这些岛屿的宣称权。他无权宣称这些岛屿，更不用说通过省略嘉智子的正式头衔来侮辱她。")]),v._v(" "),p("p",[v._v("“看来关于你美丽的传说远不及真相。”")]),v._v(" "),p("p",[v._v("嘉智子低下头。“您过奖了，”她以真正贵族应有的优雅说道。")]),v._v(" "),p("p",[v._v("“你是在说你的主人撒谎吗？”源藤开玩笑地说道。他发出爽朗的笑声。嘉智子没有进一步反驳他。“那么，告诉我。你带鹤族来，是为了让她在你见证下正式将这些岛屿割让给螳螂族吗？”他仍然微笑着问道。")]),v._v(" "),p("p",[v._v("房间里再次爆发出笑声。萤不会让他们感觉到他们正在激怒她——或者她担心嘉智子会做源藤所建议的事情。")]),v._v(" "),p("p",[v._v("“如您所知，我在这里是作为您两族之间的中立仲裁者，”嘉智子似乎提醒他。“道寺萤在这里是为了确保我的安全，因为我似乎不被允许带全部护卫。”")]),v._v(" "),p("p",[v._v("提到护卫是对螳螂族的轻轻一击——暗示他们的无礼。嘉智子现在必须在依赖鹤族或依赖螳螂族之间做出选择，她准备好接受萤的帮助了吗？")]),v._v(" "),p("p",[v._v("源藤以一声粗重的“哼”回应。他原本的愉快表情变得僵硬，转为恼怒。“正如大副无疑解释的那样，螳螂族可以很好地保护你。”")]),v._v(" "),p("p",[v._v("嘉智子现在说的话将决定他们所有人的命运。")]),v._v(" "),p("p",[v._v("“如您所说，船长。我亲眼看到螳螂族船员在港口巡逻，但没有看到佐佐木家族的武士。告诉我，他们怎么了？您到底在保护我免受谁的伤害？”")]),v._v(" "),p("p",[v._v("源藤抓住椅子的扶手。“哈！称佐佐木为武士真是抬举他们了……我们是来收取承诺给我们的货物的，但佐佐木却用刀刃对付我们，这是一种懦弱的背叛和贪婪行为！”他指着海湾的方向，声音中充满了虚假的愤怒。“当我们反击他们的进攻时，佐佐木逃跑了，而不是面对我们的力量！那些跑不掉的人，我们抓住了。我们夺取了他们的船只，以防止他们反击。”")]),v._v(" "),p("p",[v._v("他在撒谎。佐佐木家族永远不会以这种方式放弃他们的岗位，而他们在前往这个房间的路上看到的战斗痕迹也证实了这一点。像源藤这样的人当然会否认他公然袭击了鹤族的领地。整个绿岩都知道螳螂族喜欢夸大其词，与这个少数氏族的成员讨价还价时必须小心，但现在她确信，至少源藤和他的船员是简单的海盗。一个真正的武士知道诚实的重要性、诺言的力量以及对敌人礼貌的价值。")]),v._v(" "),p("p",[v._v("“多么可悲的误会，”嘉智子轻声说道。“天皇为他的仆人哭泣，”她哀叹道，但没有具体说明她指的是哪些仆人。她仍在同时玩弄源藤和萤。")]),v._v(" "),p("p",[v._v("源藤低下头表示同意。“但现在你带来了鹤族……”他示意萤，“我们可以迅速解决这个问题。”")]),v._v(" "),p("p",[v._v("嘉智子的目光集中在源藤身上，他示意西代去取墨水、卷轴和毛笔。“等一下，”她说道，放下扇子。她的嘴唇紧闭成一条细线，眼睛睁得很大。")]),v._v(" "),p("p",[v._v("这是萤第一次看到嘉智子真正感到惊讶。源藤船长是要擅自行动吗？萤本能地寻找她右边本该有的剑，但她已经交出了它们。")]),v._v(" "),p("p",[v._v("嘉智子继续说道：“我认为在做出任何正式决定之前，也应该听取佐佐木家族的证词。”")]),v._v(" "),p("p",[v._v("嘉智子还没有完全站在源藤一边，这意味着鹤族还有机会。萤仍然可以向她的顾问、她的氏族和她的父亲证明她的价值。")]),v._v(" "),p("p",[v._v("源藤几乎咆哮着回答：“在他们所做的一切之后，他们的证词还有什么价值？不——让鹤族的代表将这些岛屿割让给我们作为补偿，让我们就此了结！”")]),v._v(" "),p("p",[v._v("萤做好准备，准备击退任何胆敢强迫她在卷轴上盖章的人。嘉智子必须以某种方式阻止这一切。但她为什么要这么做呢？")]),v._v(" "),p("p",[v._v("当他的大副在他们面前展开卷轴时，嘉智子啪地合上扇子以示警告。“别忘了，船长，关于白帆堡和飞鱼群岛的管辖权，将由我来做出决定。”")]),v._v(" "),p("p",[v._v("船长与嘉智子愤怒的目光相遇。他没有退缩。")]),v._v(" "),p("p",[v._v("这是她的机会。“阁下，”萤插话道。“作为他们领主的继承人和鹤族的唯一代表，我愿意代表佐佐木家族发言。”房间里的其他人动了动。")]),v._v(" "),p("p",[v._v("萤向前走了两步，首先向嘉智子女士鞠躬，然后向源藤船长鞠躬，以示和平与尊重。这比他应得的更多。“鹤族和螳螂族和平共享一片海域已有一千年，”她开始说道。如果她希望找到共同点并赢得源藤船员的同情，她必须忽略一些争端。“飞鱼群岛的居民与丝绸和香料群岛的居民是相似的。他们都是渔民、水手、商人和天皇的忠诚仆人。”")]),v._v(" "),p("p",[v._v("称他为撒谎者会让人感到痛快，但这只会削弱她的努力。源藤需要知道，如果他试图在此刻巩固对这些岛屿的宣称权，他将面临什么风险。")]),v._v(" "),p("p",[v._v("“螳螂族风暴舰队强大，但它的船员在长途航行中依赖鹤族种植的作物来养活自己。”")]),v._v(" "),p("p",[v._v("“作为回报，鹤族购买你们的货物，并雇佣你们的许多船长。没有必要像这样抛弃鹤族这个盟友，并切断螳螂族财富的主要来源。”萤闭上眼睛，默默地向她的祖先祈祷，希望源藤能够明白道理。她深吸一口气。“释放佐佐木的囚犯。让这些岛屿恢复和平。如果你这样做，一切都可以被遗忘和原谅。”她已经说出了她的观点。她只能希望她的说服力足够。")]),v._v(" "),p("p",[v._v("一阵缓慢而嘲弄的掌声迫使她睁开眼睛。源藤懒散地坐在椅子上，双手摩擦着，显得十分愉悦。")]),v._v(" "),p("p",[v._v("“说得真好，鹤族！你几乎让我感动了。但这并不能改变佐佐木的背叛行为——你并没有否认这一点。”")]),v._v(" "),p("p",[v._v("他的荒谬说法不值得用否认来回应。嘉智子一定知道他在撒谎。")]),v._v(" "),p("p",[v._v("“船长……”嘉智子警告道。她的身体僵硬，就像在下围棋时处于下风一样。")]),v._v(" "),p("p",[v._v("“我认为是时候让阁下和我继续这场谈话了——单独谈。”他从椅子上站起来，伸出手邀请嘉智子到他身边。“你愿意和我一起参观城堡吗？从城堡顶部看日落非常壮观，我可以向你保证……”他短暂地向萤示意。“我相信你的保镖会很高兴与她的鹤族同伴团聚。”")]),v._v(" "),p("p",[v._v("他指的是地牢。作为另一个俘虏，被困住，无法为她的氏族或她的职责服务。流言蜚语将再次开始，永远毁掉她的声誉。她屏住呼吸，等待嘉智子的回答。")]),v._v(" "),p("p",[v._v("“感谢您的邀请，但我怀疑今晚的日落会被逼近的乌云破坏。”嘉智子没有起身接受他的手。")]),v._v(" "),p("p",[v._v("“哦，但还有很多鹤族留下的艺术品，你仍然可以欣赏，嘉智子。”他伸出手，毫不掩饰地邀请她晚上单独留在他的房间里。")]),v._v(" "),p("p",[v._v("“旅途劳顿，我想我会早点回房间休息，如果我们今天的谈判已经结束的话。”语气并不俏皮，她也不是在故作矜持。")]),v._v(" "),p("p",[v._v("这是她能给出的最坚定的拒绝，尽管她没有直接说出来。即使嘉智子之前已经与螳螂族达成了协议，这也不是其中的一部分。")]),v._v(" "),p("p",[v._v("萤必须在她的举动让她暴露后保护她。“帝国使者始终需要一位与她身份相称的保镖，”她提醒源藤，提高声音对所有人说道。“否则，这将等同于对天皇本人的侮辱。”")]),v._v(" "),p("p",[v._v("这是关键时刻。如果嘉智子无视萤的保护，她将有完美的机会与螳螂族谈判，远离鹤族，回到萤干涉之前的局面。但源藤谈判的代价现在似乎很清楚了。")]),v._v(" "),p("p",[v._v("嘉智子与萤的目光相遇。让我保护你，萤无声地恳求道。嘉智子的眼神柔和了下来。")]),v._v(" "),p("p",[v._v("最后，使者开口了。“确实，”她同意道，将扇子放在膝盖上。“作为鹤族继承人和翡翠冠军的女儿，只有道寺萤有资格确保帝国使者的保护。我坚持她在我的整个停留期间始终在我身边。”")]),v._v(" "),p("p",[v._v("谢谢你，萤用眼神说道，嘉智子几乎不可察觉地点了点头。她们现在站在同一战线。而且萤还没有输掉这场游戏。")]),v._v(" "),p("p",[v._v("在讲台上，源藤站起来，显得愤愤不平。他的船员们为他的爆发做好了准备。“既然如此！鹤族可以随你一起叽叽喳喳，随你高兴！你们都将作为我的客人，直到天皇为我提供款待而补偿我。我认为一万石就足够了。”")]),v._v(" "),p("p",[v._v("萤感到她的嘴巴张开了。嘉智子肯定不是来这座城堡当人质的，而一万石是一个荒谬的数目。")]),v._v(" "),p("p",[v._v("萤的战士本能被激发，她移动身体以保持嘉智子靠近。嘉智子靠向她，意识到眼前的威胁。")]),v._v(" "),p("p",[v._v("“船长——”西代打断道。")]),v._v(" "),p("p",[v._v("“安静！我们稍后再谈！”")]),v._v(" "),p("p",[v._v("源藤的傲慢、侮辱和威胁完全越界了。“你和你这群流氓怎么敢对你们的君主提出这样的要求？”萤愤怒地站起来斥责他。“你怎么敢以这种方式破坏天皇的和平，挑起与最近邻国的战争，并谎称这场冲突的起因？为什么要冒这一切的风险？”")]),v._v(" "),p("p",[v._v("源藤的眼睛因震惊和愤怒而睁大，西代也盯着她。")]),v._v(" "),p("p",[v._v("“你要把你的好运和你的盟友抛到九霄云外吗？你要向天皇陛下传达赤裸裸的蔑视吗？为什么？这对你的氏族有什么好处？想想这会对你们氏族的声誉造成的损害！”")]),v._v(" "),p("p",[v._v("一时间，周围的螳螂族水手们犹豫了，甚至源藤也被吓了一跳。西代的目光在源藤和萤之间交替。嘉智子轻轻拉了拉萤的裤子，示意她退后，仿佛在说，不要再激怒他了。")]),v._v(" "),p("p",[v._v("“我拒绝你们愚蠢的代表团是对的，你这只长脖子、自鸣得意的母鸡，”源藤喊道，并向前迈了一步。“你和你的族人不过是傲慢和愤怒！你们来这里只是为了要求和威胁，并对你们的少数氏族亲戚颐指气使。”")]),v._v(" "),p("p",[v._v("嘉智子站起来站在萤旁边。“现在听我说——”")]),v._v(" "),p("p",[v._v("源藤打断了她。“大副西代会带你们去你们的房间，这样你们就可以早点休息了。”")]),v._v(" "),p("p",[v._v("嘉智子看起来像是被扇了一巴掌。")]),v._v(" "),p("p",[v._v("作为使者，源藤本应给她说话的机会，但他不仅剥夺了这个机会，更糟糕的是，他还打断了她……")]),v._v(" "),p("p",[v._v("他不再遵守规则。萤和嘉智子失去了最后的自由，现在他们的棋子群已经被捕获。但游戏还没有结束。")]),v._v(" "),p("p",[v._v("西代从座位上跳起来，另外两名螳螂族武士紧随其后。萤扫视房间，寻找任何其他动作，或者更糟，敌意。无论有没有剑，如果有人敢对嘉智子动手，或者试图分开她们，她都会给他们一个教训。")]),v._v(" "),p("p",[v._v("“紧跟着我，”萤低声说道。")]),v._v(" "),p("p",[v._v("嘉智子点点头，走到她身边。")])])}),[],!1,null,null,null);_.default=e.exports}}]);